import React, {useState}  from 'react'
// import { useGetData } from '../../custom-hooks'
import { Link } from 'react-router-dom';
import {Paper} from '@material-ui/core';
import { Drawer as MUIDrawer, 
    ListItem, 
    List, 
    ListItemIcon, 
    ListItemText, 
    Theme,
    useTheme, 
    makeStyles, 
    createStyles,
    AppBar,
    Toolbar,
    IconButton,
    Typography,
    Divider,
    Button,
    Dialog, 
    DialogActions, 
    DialogContent,
    DialogContentText, 
    DialogTitle  ,
} from '@material-ui/core';
import CssBaseline from '@material-ui/core/CssBaseline';
import MenuIcon from '@material-ui/icons/Menu'
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import ChevronRightIcon from '@material-ui/icons/ChevronRight'
import clsx from 'clsx';
import { RouteComponentProps, withRouter, Switch, Route } from "react-router-dom";
// import { DataTable } from '../../components/DataTable';
// import { ContactForm } from '../ContactForm'

interface PhonebookProps{
  history: RouteComponentProps["history"];
  location: RouteComponentProps["location"];
  match: RouteComponentProps["match"];
}

const drawerWidth = 240;

const useStyles = makeStyles((theme: Theme) => createStyles({
  main: {
      display: 'flex',
      flexDirection: 'column',
      backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.5))`,
      width: '100%',
      height: '100%',
      position: 'absolute',
      justifyContent: 'center',
      alignItems: 'center',
      fontFamily: "Georgia, 'Times New Roman', Times, serif",
  },
  h3: {
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'center',
      color: 'black',
  },
  text: {
      display: 'flex',
      flexDirection: 'column',
      color: '#4ef3b4',
      textDecoration: 'none',
      backgroundColor: 'white',
      width: '60%',
      justifyContent: 'center',
      alignItems: 'center',
  },
  logo:{
      margin: '0 0 0 0.45em',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'center',
  },
  logo_a: {
      color: '#4ef3b4',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'center',
  },
  logo_navigation: {
      listStyle: 'none',
      textTransform: 'uppercase',
      textDecoration: 'none',
  },
  root: {
    display: 'flex',
  },
  column: {
      display: 'flex',
      flexDirection: 'column',
  },
  // appBar: {
  //   transition: theme.transitions.create(['margin', 'width'], {
  //     easing: theme.transitions.easing.sharp,
  //     duration: theme.transitions.duration.leavingScreen,
  //   }),
  // },
  // appBarShift: {
  //   width: `calc(100% - ${drawerWidth}px)`,
  //   marginLeft: drawerWidth,
  //   transition: theme.transitions.create(['margin', 'width'], {
  //     easing: theme.transitions.easing.easeOut,
  //     duration: theme.transitions.duration.enteringScreen,
  //   }),
  // },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  hide: {
    display: 'none',
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0,
  },
  // drawerPaper: {
  //   width: drawerWidth,
  // },
  // drawerHeader: {
  //   display: 'flex',
  //   alignItems: 'center',
  //   padding: theme.spacing(0, 1),
  //   // necessary for content to be below app bar
  //   ...theme.mixins.toolbar,
  //   justifyContent: 'flex-end',
  // },
  // content: {
  //   flexGrow: 1,
  //   padding: theme.spacing(3),
  //   transition: theme.transitions.create('margin', {
  //     easing: theme.transitions.easing.sharp,
  //     duration: theme.transitions.duration.leavingScreen,
  //   }),
  //   marginLeft: -drawerWidth,
  // },
  // contentShift: {
  //   transition: theme.transitions.create('margin', {
  //     easing: theme.transitions.easing.easeOut,
  //     duration: theme.transitions.duration.enteringScreen,
  //   }),
  //   marginLeft: 0,
  // },
  toolbar:{
    display: 'flex',
    backgroundColor: '#003155',
    color: 'white',
    fontFamily: "Georgia, 'Times New Roman', Times, serif",
  },
  toolbar_button: {
    marginLeft: 'auto',
    color: 'white',
    fontFamily: "Georgia, 'Times New Roman', Times, serif",
  },
  margin_top: {
      marginTop: '50px',
  },
  font: {
      fontFamily: "Georgia, 'Times New Roman', Times, serif",
  },
  leftMargin: {
      marginLeft: '240px',
  },
}))


export const Phonebook = withRouter(( props:PhonebookProps ) => {
  console.log(props);
  const { history } = props;
  const classes = useStyles();
  const theme = useTheme();
  const [open, setOpen] = useState(false);

  const [dialogOpen, setDialogOpen] = useState(false);

  const handleDrawerOpen = () => {
    setOpen(true);
  };

  const handleDrawerClose = () => {
    setOpen(false)
  };
  const handleDialogClickOpen = () => {
    setDialogOpen(true)
  };

  const handleDialogClickClose = () => {
      setDialogOpen(false)
  };

  const itemsList = [
    {
        text: 'Home',
        onClick: () => history.push('/')
    },
    {
        text: 'Sign In',
        onClick: () => history.push('/signin')
    },
    {
        text: 'About',
        onClick: () => history.push('/about')
    },
    {
        text: 'Contact',
        onClick: () => history.push('/contact')
    },
  ];
  return (
    <div className={`${classes.root} ${classes.column}`}>
        <CssBaseline /> {/* This is just a normal part of complex MUI; see docs */}
        <AppBar position="fixed" className={clsx(classes.appBar, {[classes.appBarShift]: open })}> {/* We are using MUI to make another navbar */}
            <Toolbar className={classes.toolbar}>
                <IconButton color="inherit" aria-label="open-drawer" onClick={handleDrawerOpen} edge="start" className={clsx(classes.menuButton, open && classes.hide)}>
                    <MenuIcon />
                </IconButton>
                <Typography variant="h6" className={classes.font} noWrap>
                    My Phonebook
                </Typography>
                <Button className={classes.toolbar_button} onClick={handleDialogClickOpen}>Create New Contact</Button>

                {/* Dialog Pop Up */}
                <Dialog open={dialogOpen} onClose={handleDialogClickClose} aria-labelledby="form-dialog-title">
                    <DialogTitle id="form-dialog-title">Add New Contact</DialogTitle>
                    <DialogContent>
                        <DialogContentText></DialogContentText>
                        <ContactForm />
                    </DialogContent>
                    <DialogActions>
                        <Button onClick={handleDialogClickClose} color="primary">Cancel</Button>
                        <Button onClick={handleDialogClickOpen} color="primary">Done</Button>
                    </DialogActions>
                </Dialog>
                {/* End dialog pop-up */}
            </Toolbar>
        </AppBar>
        <MUIDrawer className={classes.drawer} variant="persistent" anchor="left" open={open} classes={{paper: classes.drawerPaper,}}>
            <div className={classes.drawerHeader}>
                <IconButton onClick={handleDrawerClose}>
                    {theme.direction === 'ltr' ? <ChevronLeftIcon /> : <ChevronRightIcon />}
                </IconButton>
            </div>
            <Divider /> {/* A fun educational game is to comment elements out while the compiler is running and see what disappears or breaks */}
            <List>
                {itemsList.map((item, index) => { {/* don't get lost here, it's just a map function looping over our itemsList array! */}
                    const { text, onClick } = item;
                    return (
                        <ListItem button key={text} onClick={onClick}>
                            <ListItemText primary={text} />
                        </ListItem>
                    );
                })}
            </List>
        </MUIDrawer>
        <main className={`${clsx(classes.content, {[classes.contentShift]: open,})} ${classes.leftMargin}`}>
            <div className={classes.drawerHeader} />
                {/* <DataTable /> */} {/* Keep this commented out for now */}
        </main>
    </div>
)
});
